mapper.insert:
INSERT INTO MEMBER (
    MEMBER_ID
    , PASSWORD
    , NAME
    , AGE
    , PHONE_NUM
    , EMAIL
    , CREATE_DATE
    , UPDATE_DATE
) VALUES (
    #{memberId}
    , #{password}
    , #{name}
    , #{age}
    , #{phoneNum}
    , #{email}
    , SYSDATE
    , NULL
)

mapper.update:
UPDATE MEMBER SET
    PASSWORD = #{password}
    , NAME = #{name}
    , AGE = #{age}
    , PHONE_NUM = #{phoneNum}
    , EMAIL = #{email}
    , UPDATE_DATE = SYSDATE
WHERE ID = #{id}
